mapper.select:
select * from food_foodtype

     and typeId=#{typeId} 
     and typeName like concat('%', #{typeName}, '%') 
     and typeStatus=#{typeStatus}

mapper.update:
update food_foodtype
typeName=#{typeName}, typeImage=#{typeImage}, typeDesc=#{typeDesc}, lastModifyBy=#{lastModifyBy}, lastModifyTime=NOW(),
where typeId=#{typeId}
