mapper.select:
select * from food_tickettype

     and ticketTypeId=#{ticketTypeId} 
     and ticketName like concat('%', #{ticketName}, '%') 
     and requirement=#{requirement} 
     and cheap=#{cheap} 
     and liveTime=#{liveTime}

mapper.update:
update food_tickettype
ticketName=#{ticketName}, requirement=#{requirement}, cheap=#{cheap}, liveTime=#{liveTime}, ticketNum=#{ticketNum}, lastModifyBy=#{lastModifyBy}, lastModifyTime=NOW(),
where ticketTypeId=#{ticketTypeId}
