mapper.select:
select * from food_user

     and userId=#{userId} 
     and username like concat('%', #{username}, '%') 
     and phone like concat('%', #{phone}, '%') 
     and email like concat('%', #{email}, '%') 
     and gender=#{gender}

mapper.insert:


    select last_insert_id()

insert into food_user (username, password, phone, email, birthday, gender, registerDate)
values(#{username}, #{password}, #{phone}, #{email}, #{birthday}, #{gender}, NOW());

mapper.update:
update food_user
username=#{username}, phone=#{phone}, birthday=#{birthday}, email=#{email}, gender=#{gender}, authFlag=#{authFlag},
where userId=#{userId}
